definition.script:
pipeline {
    agent any

    tools {
        maven 'maven3.6'
        jdk 'jdk17'
    }

    parameters {
        string(
            name: 'BRANCH_NAME',
            defaultValue: 'main',
            description: 'Enter the branch name to build'
        )

        choice(
            name: 'ENVIRONMENT',
            choices: ['DEV', 'QA', 'PROD'],
            description: 'Select the environment'
        )

        booleanParam(
            name: 'RUN_TESTS',
            defaultValue: true,
            description: 'Run Maven tests?'
        )
    }

    stages {

        stage('Checkout Code') {
            steps {
                echo "Checking out branch: ${params.BRANCH_NAME}"
                git branch: "${params.BRANCH_NAME}",
                    url: 'https://github.com/kishanops/Boardgame.git'
            }
        }

        stage('Build') {
            steps {
                echo "Building for ${params.ENVIRONMENT} environment"
                sh 'mvn clean package'
            }
        }

        stage('Test') {
            when {
                expression { params.RUN_TESTS == true }
            }
            steps {
                echo "Running Tests..."
                sh 'mvn test'
            }
        }
    }
}
